DROP TABLE IF EXISTS `wp_TABLE_STATISTICS_RAW`;
CREATE TABLE `wp_TABLE_STATISTICS_RAW` (
  `remote_addr` int(10) NOT NULL default '0',
  `remote_host` varchar(32) default NULL,
  `http_user_agent` varchar(128) default '',
